function ProcessLike(n){SendRequest("/Home/SetLikeToComment",{articleId:n});$("#like").attr("disabled",!1)}function ProcessComment(n){var t=$("#commentFiled").val();SendRequest("/Home/SetComment",{articleId:n,text:t})}function SendRequest(n,t){$.ajax({type:"POST",url:n,data:t,success:function(n){n==!0&&location.reload()}})}